CREATE TABLE mdi.edw_join_tree_definition (
											  dwid BIGSERIAL NOT NULL
												  CONSTRAINT pk_edw_join_tree_definition
													  PRIMARY KEY,
											  root_join_dwid BIGINT NOT NULL
												  CONSTRAINT fk_join_tree_definition_1
													  REFERENCES mdi.edw_join_definition (dwid)
													  ON UPDATE RESTRICT
													  ON DELETE RESTRICT
													  DEFERRABLE INITIALLY DEFERRED,
											  sibling_join_tree_dwid BIGINT
												  CONSTRAINT fk_join_tree_definition_2
													  REFERENCES mdi.edw_join_tree_definition (dwid)
													  ON UPDATE RESTRICT
													  ON DELETE RESTRICT
													  DEFERRABLE INITIALLY DEFERRED,
											  child_join_tree_dwid BIGINT
												  CONSTRAINT fk_join_tree_definition_3
													  REFERENCES mdi.edw_join_tree_definition (dwid)
													  ON UPDATE RESTRICT
													  ON DELETE RESTRICT
													  DEFERRABLE INITIALLY DEFERRED
										  );

-- add new columns to edw_field_definition
ALTER TABLE mdi.edw_field_definition
    ADD COLUMN field_source_calculation TEXT,
    DROP COLUMN source_edw_join_definition_dwid,
    ADD COLUMN source_edw_join_tree_definition_dwid  BIGINT
		CONSTRAINT fk_edw_field_definition_4
			REFERENCES mdi.edw_join_tree_definition (dwid)
			ON UPDATE RESTRICT
			ON DELETE RESTRICT
			DEFERRABLE INITIALLY DEFERRED,
	ADD COLUMN data_type TEXT,
	ADD COLUMN reporting_label TEXT,
	ADD COLUMN reporting_description TEXT,
	ADD COLUMN reporting_hidden TEXT,
	ADD COLUMN reporting_key_flag BOOLEAN;

