INSERT INTO mdi.edw_table_definition (database_name, schema_name, table_name, primary_source_edw_table_definition_dwid)
SELECT
	'staging'
	, tstd.schema_name
	, tstd.table_name
	, etd.dwid
FROM
	temp_star_table_data tstd
