-- populate edw_join_tree_definition with a single-node join tree for each simple source join in the field dataset
INSERT
INTO mdi.edw_join_tree_definition (root_join_dwid)
SELECT DISTINCT
	ejd.dwid
FROM
	temp_star_field_data sfd
		INNER JOIN mdi.edw_join_definition ejd
		ON ejd.join_type = sfd.join_type AND
		   sfd.join_condition = ejd.join_condition;

-- actually populate the edw_field_definition table now that the correct join tree dwids can be pulled in
INSERT
INTO mdi.edw_field_definition ( edw_table_definition_dwid, field_name, key_field_flag, source_edw_field_definition_dwid
							  , field_source_calculation, source_edw_join_tree_definition_dwid, data_type, reporting_label
							  , reporting_hidden, reporting_key_flag)
SELECT
	tgt_etd.dwid AS edw_table_definition_dwid
	, sfd.field_name
	, sfd.key_field_flag
	, src_efd.dwid AS source_edw_field_definition_dwid
    , sfd.field_source_calculation AS field_source_calculation
	, ejtd.dwid AS source_edw_join_tree_definition_dwid
	, sfd.data_type
	, sfd.reporting_label
	, sfd.reporting_hidden
	, sfd.reporting_key_flag
FROM
	temp_star_field_data sfd
		INNER JOIN mdi.edw_table_definition tgt_etd
		ON tgt_etd.schema_name = sfd.schema_name AND tgt_etd.table_name = sfd.table_name
		LEFT JOIN mdi.edw_table_definition src_etd
		ON src_etd.schema_name = sfd.source_schema_name
			AND src_etd.table_name = sfd.source_table_name
		LEFT JOIN mdi.edw_field_definition src_efd
		ON src_etd.dwid = src_efd.edw_table_definition_dwid AND src_efd.field_name = sfd.source_column_name
		LEFT JOIN mdi.edw_join_definition ejd
		ON ejd.join_type = sfd.join_type AND ejd.join_condition = sfd.join_condition
		LEFT JOIN mdi.edw_join_tree_definition ejtd
		ON ejd.dwid = ejtd.root_join_dwid;

-- add multi-stage join dwids to the three fields that require them
WITH
	proposal_deal_join AS (
		INSERT INTO mdi.edw_join_tree_definition (root_join_dwid)
			SELECT
				dwid
			FROM
				mdi.edw_join_definition
			WHERE
					join_condition = 'proposal.prp_deal_pid = deal.d_pid'
			RETURNING dwid
	)
	, loan_proposal_join AS (
	INSERT INTO mdi.edw_join_tree_definition (root_join_dwid, child_join_tree_dwid)
		SELECT
			edw_join_definition.dwid
			, proposal_deal_join.dwid
		FROM
			mdi.edw_join_definition
				CROSS JOIN proposal_deal_join
		WHERE
				join_condition = 'loan.l_proposal_pid = proposal.prp_pid'
		RETURNING dwid
)
UPDATE mdi.edw_field_definition efd
SET
	source_edw_join_tree_definition_dwid = loan_proposal_join.dwid
FROM
	mdi.edw_table_definition etd
		CROSS JOIN loan_proposal_join
WHERE
	(efd.edw_table_definition_dwid = etd.dwid)
	AND ((table_name = 'loan_dim' AND field_name = 'los_loan_number')
	OR (table_name = 'loan_junk_dim' AND field_name = 'mi_required_flag')
	OR (table_name = 'loan_junk_dim' AND field_name = 'piggyback_flag'));

-- add new join between mortgage_insurance_dim.loan_dwid and loan_dim.dwid
WITH
	dwids AS (
		SELECT *
		FROM
			(
				SELECT
					etd.dwid AS loan_table_dwid
					, efd.dwid AS loan_field_dwid
				FROM
					mdi.edw_field_definition efd
						INNER JOIN mdi.edw_table_definition etd
						ON efd.edw_table_definition_dwid = etd.dwid
				WHERE table_name = 'loan_dim' AND field_name = 'dwid'
			) AS loan_dwids
				CROSS JOIN (
				SELECT
					etd.dwid AS mi_table_dwid
					, efd.dwid AS mi_field_dwid
				FROM
					mdi.edw_field_definition efd
						INNER JOIN mdi.edw_table_definition etd
						ON efd.edw_table_definition_dwid = etd.dwid
				WHERE table_name = 'mortgage_insurance_dim' AND field_name = 'loan_dwid'
			) AS mi_dwids
	)
	, inserted_join AS (
	INSERT INTO mdi.edw_join_definition (primary_edw_table_definition_dwid, target_edw_table_definition_dwid,
									 join_type, join_condition)
		SELECT
			mi_table_dwid AS primary_edw_table_definition_dwid
			, loan_table_dwid AS target_edw_table_definition_dwid
			, 'inner' AS join_type
			, 'mortgage_insurance_dim.loan_dwid = loan_dim.dwid' AS join_condition
		FROM
			dwids
		RETURNING dwid
)
	, inserted_join_tree AS (
	INSERT INTO mdi.edw_join_tree_definition (root_join_dwid)
		SELECT
			ij.dwid
		FROM
			inserted_join ij
		RETURNING dwid
)
UPDATE mdi.edw_field_definition efd
SET
	source_edw_join_tree_definition_dwid = isj.dwid
	, source_edw_field_definition_dwid = d.loan_field_dwid
FROM
	inserted_join_tree isj
		CROSS JOIN dwids d
WHERE
	efd.dwid = d.mi_field_dwid;

--
-- EDW | Modify staging_octane to history_octane join conditions to use aliased table names
-- (https://app.asana.com/0/0/1200274682108259/)
--


update mdi.edw_join_definition
set join_condition = REPLACE (REPLACE (join_condition, ' = '||target_table.table_name || '.', ' = t'||edw_join_definition.dwid||'.'), primary_table.table_name || '.', 'primary_table.')
from mdi.edw_table_definition as primary_table
   , mdi.edw_table_definition as target_table
where edw_join_definition.primary_edw_table_definition_dwid = primary_table.dwid
  and edw_join_definition.target_edw_table_definition_dwid = target_table.dwid
